import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Reservation = () => {
  const { id } = useParams();
  const [equipment, setEquipment] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:4000/api/equipment/${id}`)
      .then((response) => {
        setEquipment(response.data);
      })
      .catch((error) => console.error('Błąd podczas pobierania danych sprzętu:', error));
  }, [id]);

  const formatDate = (date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = (`0${d.getMonth() + 1}`).slice(-2);
    const day = (`0${d.getDate()}`).slice(-2);
    return `${year}/${month}/${day}`;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedStartDate = formatDate(startDate);
    const formattedEndDate = formatDate(endDate);
    axios.post('http://localhost:4000/api/booking', { equipmentId: id, startDate: formattedStartDate, endDate: formattedEndDate })
      .then(() => setSuccess(true))
      .catch((error) => console.error('Błąd podczas rezerwacji:', error));
  };

  const today = new Date().toISOString().split('T')[0];

  return (
    <div>
      <h1>Rezerwuj sprzęt</h1>
      {equipment && <h2>{equipment.Name}</h2>}
      {success ? (
        <p>Rezerwacja zakończona sukcesem!</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="startDate">Data rozpoczęcia:</label>
            <input
              type="date"
              id="startDate"
              value={startDate}
              min={today} // Ustaw minimalną datę na dzisiejszą datę
              onChange={(e) => setStartDate(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="endDate">Data zakończenia:</label>
            <input
              type="date"
              id="endDate"
              value={endDate}
              min={startDate || today} // Ustaw minimalną datę na datę rozpoczęcia lub dzisiejszą datę
              onChange={(e) => setEndDate(e.target.value)}
              required
            />
          </div>
          <button type="submit">Rezerwuj</button>
        </form>
      )}
    </div>
  );
};

export default Reservation;
