import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background-color: #f0f8ff;
  min-height: 100vh;
`;

const Form = styled.form`
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  width: 300px;
  margin-top: 20px;
`;

const Title = styled.h1`
  color: #0077b6;
`;

const Subtitle = styled.h2`
  color: #0077b6;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 10px;
  font-size: 14px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 14px;
`;

const Button = styled.button`
  background-color: #0077b6;
  color: white;
  border: none;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  border-radius: 5px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #005f8a;
  }
`;

const ErrorMessage = styled.p`
  color: red;
  font-size: 14px;
`;

const Reservation = () => {
  const { id } = useParams();
  const [equipment, setEquipment] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [success, setSuccess] = useState(false);
  const [showUserForm, setShowUserForm] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    axios.get(`http://localhost:4000/api/equipment/${id}`)
      .then((response) => {
        setEquipment(response.data);
      })
      .catch((error) => console.error('Błąd podczas pobierania danych sprzętu:', error));
  }, [id]);

  const calculateTotalPrice = (start, end) => {
    const startDate = new Date(start);
    const endDate = new Date(end);
    const days = (endDate - startDate) / (1000 * 60 * 60 * 24) + 1;
    return days * equipment.PricePerDay;
  };

  const handleSubmitDates = (e) => {
    e.preventDefault();
    const price = calculateTotalPrice(startDate, endDate);
    setTotalPrice(price);
    setShowUserForm(true);
  };

  const handleSubmitReservation = (e) => {
    e.preventDefault();
    axios.post('http://localhost:4000/api/booking', {
      equipmentId: id,
      startDate,
      endDate,
      firstName,
      lastName,
      email,
      phone,
      totalPrice
    })
      .then(() => setSuccess(true))
      .catch((error) => console.error('Błąd podczas rezerwacji:', error));
  };

  const today = new Date().toISOString().split('T')[0];

  return (
    <Container>
      <Title>Rezerwuj sprzęt</Title>
      {equipment && <Subtitle>{equipment.Name}</Subtitle>}
      {success ? (
        <p>Rezerwacja zakończona sukcesem!</p>
      ) : showUserForm ? (
        <Form onSubmit={handleSubmitReservation}>
          <div>
            <Label htmlFor="firstName">Imię:</Label>
            <Input
              type="text"
              id="firstName"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
          </div>
          <div>
            <Label htmlFor="lastName">Nazwisko:</Label>
            <Input
              type="text"
              id="lastName"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>
          <div>
            <Label htmlFor="email">E-mail:</Label>
            <Input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <Label htmlFor="phone">Nr telefonu:</Label>
            <Input
              type="tel"
              id="phone"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </div>
          <div>
            <p>Kwota końcowa: {totalPrice} PLN</p>
          </div>
          <Button type="submit">Potwierdź rezerwację</Button>
        </Form>
      ) : (
        <Form onSubmit={handleSubmitDates}>
          <div>
            <Label htmlFor="startDate">Data rozpoczęcia:</Label>
            <Input
              type="date"
              id="startDate"
              value={startDate}
              min={today} // Ustaw minimalną datę na dzisiejszą datę
              onChange={(e) => setStartDate(e.target.value)}
              required
            />
          </div>
          <div>
            <Label htmlFor="endDate">Data zakończenia:</Label>
            <Input
              type="date"
              id="endDate"
              value={endDate}
              min={startDate || today} // Ustaw minimalną datę na datę rozpoczęcia lub dzisiejszą datę
              onChange={(e) => setEndDate(e.target.value)}
              required
            />
          </div>
          <Button type="submit">Rezerwuj</Button>
        </Form>
      )}
    </Container>
  );
};

export default Reservation;
