import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 20px;
`;

const Title = styled.h1`
  color: #0077b6;
`;

const ReservationList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const ReservationItem = styled.li`
  background-color: #f0f8ff;
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 5px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const Button = styled.button`
  background-color: #0077b6;
  color: white;
  border: none;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  border-radius: 5px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #005f8a;
  }
`;

const AdminPanel = () => {
  const [rentals, setRentals] = useState([]);

  useEffect(() => {
    // Pobierz dane z endpointu `api/rentals`
    axios.get('http://localhost:4000/api/rentals')
      .then((response) => {
        setRentals(response.data);
      })
      .catch((error) => console.error('Błąd podczas pobierania wypożyczeń:', error));
  }, []);

  const handleEndRental = (id) => {
    axios.put(`http://localhost:4000/api/rentals/${id}/end`)
      .then((response) => {
        setRentals(rentals.map(rental => rental.id === id ? { ...rental, RentalStatus: 'ended' } : rental));
      })
      .catch((error) => console.error('Błąd podczas kończenia rezerwacji:', error));
  };

  return (
    <Container>
      <Title>Panel Admina</Title>
      <h2>Wszystkie rezerwacje</h2>
      <ReservationList>
        {rentals.map((rental) => (
          <ReservationItem key={rental.id}>
            <p>Sprzęt ID: {rental.EquipmentID}</p>
            <p>Imię: {rental.FirstName}</p>
            <p>Nazwisko: {rental.LastName}</p>
            <p>Email: {rental.Email}</p>
            <p>Nr telefonu: {rental.Phone}</p>
            <p>Data rozpoczęcia: {rental.RentalStartDate}</p>
            <p>Data zakończenia: {rental.RentalEndDate}</p>
            <p>Kwota końcowa: {rental.TotalPrice} PLN</p>
            <p>Status: {rental.RentalStatus}</p>
            {rental.RentalStatus !== 'Za' && (
              <Button onClick={() => handleEndRental(rental.id)}>Zakończ rezerwację</Button>
            )}
          </ReservationItem>
        ))}
      </ReservationList>
    </Container>
  );
};

export default AdminPanel;
